import React , {useEffect, useState} from "react";
import AddDriver from './AddDriver';
import {
    Breadcrumb,
    BreadcrumbItem,
    Button,
    Card,
    CardBody,
    CardTitle,
    Container,
    Row,
    Col,
    FormGroup,
    Input
  } from "reactstrap";
import { useForm } from "react-hook-form";
import _ from "lodash/fp";
import { fetchDriver, switchEditMode } from '../../store/actions';
import { connect } from "react-redux";


function Driver(props){

	const { register, handleSubmit, watch, errors } = useForm();
	const driverId =  props.match.params.id;
	const [driver, setDriver]= useState([]);
	useEffect(() => {
		let currentDriver= props.drivers.find((item) => item.id == driverId);
		setDriver(currentDriver);
	 },[]);

	  const editHandler = () => {
	    props.switchEditMode();
	  }
	const onSubmit =  data  => {
      console.log(data);
    }
	return(
		<Container fluid>
			<h1>driver view page</h1>
			{
              !props.editMode ?
                <div className="edit-btn-right">
                  <Button
                    onClick={editHandler}
                    className="btn btn-info"
                  >
                    Edit details
                      </Button>
                </div>
                : null
            }
			<Card className="mb-4">
                <CardBody>
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <Row>
                            <Col md="4">
                                <FormGroup>
                                    <label
                                      className="form-control-label"
                                      htmlFor="firstname"
                                    >
                                    Name
                                    </label>
                                    <Input
                                      id="name"
                                      name="name"
                                      placeholder="Name"
                                      type="text"
                                      value={driver.name}
                                      disabled={!props.editMode}
                                      innerRef={register({
                                          required: true,
                                          pattern: /^[A-Za-z]+[A-Za-z]/i 
                                        })}
                                    />
                                    {_.get("name.type", errors) === "required" && (
                                        <p className="error-message">This field is required</p>
                                    )}
                                    {_.get("name.type", errors) === "pattern" && (
                                        <p className="error-message">Name should be letters only</p>
                                    )}
                                </FormGroup>
                            </Col>
                            <Col md="4">
                                <FormGroup>
                                    <label
                                      className="form-control-label"
                                      htmlFor="email"
                                    >
                                    Email
                                    </label>
                                    <Input
                                      name="email"
                                      placeholder="Email"
                                      value={driver.email}
                                      type="email"
                                      disabled={!props.editMode}
                                      innerRef={register({
                                      required: true,
                                      pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i
                                    })}
                                    />
                                    {_.get("email.type", errors) === "required" && (
                                        <p className="error-message">This field is required</p>
                                    )}
                                    {_.get("email.type", errors) === "pattern" && (
                                       <p className="error-message">Invalid Email</p>
                                    )}
                                </FormGroup>
                            </Col>
                            <Col md="4">
                                <FormGroup>
                                    <label
                                      className="form-control-label"
                                      htmlFor="lastname"
                                    >
                                    Address
                                    </label>
                                    <Input
                                      name="address"
                                      placeholder="Address"
                                      type="text"
                                      disabled={!props.editMode}
                                      value={driver.address}
                                      innerRef={register({
                                          required: true,
                                        })}
                                    />
                                     {_.get("address.type", errors) === "required" && (
                                        <p className="error-message">This field is required</p>
                                    )}
                                </FormGroup>
                            </Col>
                        </Row>
                        <Row>

                            <Col md="4">
                                <FormGroup>
                                    <label
                                      className="form-control-label"
                                      htmlFor="lastname"
                                    >
                                    Phone
                                    </label>
                                    <Input
                                      name="phone"
                                      placeholder="Phone No"
                                      type="number"
                                      value={driver.phone}
                                      disabled={!props.editMode}
                                      innerRef={register({
                                          required: true,
                                        })}
                                    />
                                     {_.get("phone.type", errors) === "required" && (
                                        <p className="error-message">This field is required</p>
                                    )}
                                </FormGroup>
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                             {props.editMode ?
                                <Button type="submit" className="btn-info">Update</Button>
                                :null}
                            </Col>
                        </Row>
                    </form>
                </CardBody>
            </Card>
		</Container>
	);
}
const mapStateToProps = (state) => {
  return {
    drivers:state.driver.drivers,
    editMode:state.driver.editMode
  };
};

export default connect(mapStateToProps, { fetchDriver, switchEditMode })(Driver);
