import React from "react";
import classnames from "classnames";
import "./login.scss";
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col
} from "reactstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelopeOpenText, faLockOpen} from '@fortawesome/free-solid-svg-icons';
import { useForm } from "react-hook-form";

function Login () {

  const { login, handleSubmit, errors } = useForm();
  const onSubmit = data => console.log(data);
    return (
      <>
        <div className="login-form-wrapper">
                <Card className="border-0 mb-0">
                  <CardBody className="px-lg-5 py-lg-5">
                    <div className="text-center text-muted mb-4">
                      <small>Login Form</small>
                    </div>
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <input name="email" ref={login({ 
                            required:true ,
                            maxLength: 20,
                            pattern: /^[A-Za-z]+$/i
                          })} 
                        />
                        <input name="password" ref={login({ required: true })} />
                        {errors.exampleRequired && <span>This field is required</span>}
                        <input type="submit" />
                      </form>

                  </CardBody>
                </Card>
                <Row className="mt-3">
                  <Col xs="6">
                    <a
                      className="text-light"
                      href="#pablo"
                      onClick={e => e.preventDefault()}
                    >
                      <small>Forgot password?</small>
                    </a>
                  </Col>
                  <Col className="text-right" xs="6">
                    <a
                      className="text-light"
                      href="#pablo"
                      onClick={e => e.preventDefault()}
                    >
                      <small>Create new account</small>
                    </a>
                  </Col>
                </Row>
        </div>
      </>
    );
}

export default Login;
