import React , {useEffect} from "react";
import AddDriver from './AddDriver';
import {
    Breadcrumb,
    BreadcrumbItem,
    Button,
    Card,
    CardBody,
    CardTitle,
    Container,
    Row,
    Col,
    FormGroup,
    Input
  } from "reactstrap";
import { useForm } from "react-hook-form";
import _ from "lodash/fp";
import fetchDriver from '../../store/actions';


function Driver(props){

	const { register, handleSubmit, watch, errors } = useForm();
	const driverId =  props.match.params.id;
	console.log(driverId)

	useEffect(() => {
	    props.fetchDriver(driverId);
	 },[]);
	const onSubmit =  data  => {
      console.log(data);
    }
	return(
		<Container fluid>
			<h1>driver view page</h1>
			<Card className="mb-4">
                <CardBody>
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <Row>
                            <Col md="4">
                                <FormGroup>
                                    <label
                                      className="form-control-label"
                                      htmlFor="firstname"
                                    >
                                    Name
                                    </label>
                                    <Input
                                      id="name"
                                      name="name"
                                      placeholder="Name"
                                      type="text"
                                      innerRef={register({
                                          required: true,
                                          pattern: /^[A-Za-z]+[A-Za-z]/i 
                                        })}
                                    />
                                    {_.get("name.type", errors) === "required" && (
                                        <p className="error-message">This field is required</p>
                                    )}
                                    {_.get("name.type", errors) === "pattern" && (
                                        <p className="error-message">Name should be letters only</p>
                                    )}
                                </FormGroup>
                            </Col>
                            <Col md="4">
                                <FormGroup>
                                    <label
                                      className="form-control-label"
                                      htmlFor="email"
                                    >
                                    Email
                                    </label>
                                    <Input
                                      name="email"
                                      placeholder="Email"
                                      type="email"
                                      innerRef={register({
                                      required: true,
                                      pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i
                                    })}
                                    />
                                    {_.get("email.type", errors) === "required" && (
                                        <p className="error-message">This field is required</p>
                                    )}
                                    {_.get("email.type", errors) === "pattern" && (
                                       <p className="error-message">Invalid Email</p>
                                    )}
                                </FormGroup>
                            </Col>
                            <Col md="4">
                                <FormGroup>
                                    <label
                                      className="form-control-label"
                                      htmlFor="lastname"
                                    >
                                    Address
                                    </label>
                                    <Input
                                      name="address"
                                      placeholder="Address"
                                      type="text"
                                      innerRef={register({
                                          required: true,
                                        })}
                                    />
                                     {_.get("address.type", errors) === "required" && (
                                        <p className="error-message">This field is required</p>
                                    )}
                                </FormGroup>
                            </Col>
                        </Row>
                        <Row>

                            <Col md="4">
                                <FormGroup>
                                    <label
                                      className="form-control-label"
                                      htmlFor="lastname"
                                    >
                                    Phone
                                    </label>
                                    <Input
                                      name="phone"
                                      placeholder="Phone No"
                                      type="number"
                                      innerRef={register({
                                          required: true,
                                        })}
                                    />
                                     {_.get("phone.type", errors) === "required" && (
                                        <p className="error-message">This field is required</p>
                                    )}
                                </FormGroup>
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Button type="submit" className="btn-info">Submit</Button>
                            </Col>
                        </Row>
                    </form>
                </CardBody>
            </Card>
		</Container>
	);
}
export default Driver;
