import React, { useState, useEffect} from 'react';
import { MDBDataTableV5 } from 'mdbreact';
import { Button, Container } from 'reactstrap';
import { Link } from 'react-router-dom';
import './driver.scss';
import { connect } from "react-redux";
import { fetchDriver, deleteDriver } from '../../store/actions';

function DriverList(props) {
  const [data, setData] = useState([])

  const  deleteDriver = async () => {
    try {
      const deleteResponse = await API(`drivers/${props.drivers.id}`,'delete');
    } catch (error) {
      setErrorMessage("Server Error!");
    }
  };

  let newList = [];
  if (props.drivers){
       newList = props.drivers.map(drivers => {
        return Object.assign(drivers, {
            action: 
            <>
              <button className="btn btn-primary">Edit</button>
              <button className="btn btn-danger" onClick={deleteDriver}>Delete</button>
            </>
        });
    });
  }

  const newData={
    columns: [
      {
        label: 'Name',
        field: 'name',
        width: 100,
      },
      {
        label: 'Email',
        field: 'email',
        width: 100,
      },
      {
        label: 'Address',
        field: 'address',
        width: 100,
      },
      {
        label: 'Phone No.',
        field: 'phone',
        width: 100,
      },
      {
        label: 'Action',
        field: 'action',
        width: 200,
      },
    ],
    rows: newList
  };

  useEffect(() => {
      props.fetchDriver();
  },[]);

    return(
    <>
      <div className="driver-list">
        <Container fluid>
          <h1>driver list</h1>
          <MDBDataTableV5 hover entriesOptions={[5, 20, 25]} entries={20} pagesAmount={4} data={newData} />
        </Container>
      </div>
    </>
  )
}

const mapStateToProps = (state) => {
  return {
    drivers: state.driver.drivers,
  };
};
export default connect(mapStateToProps, { fetchDriver })(DriverList);
