import React, { useState } from 'react';
import { Alert} from 'reactstrap';
import * as alertMessage from './AlertMessages';

function AlertBox(){
	const[alert, setAlert] = useState(false)

	const close=()=>{
		setAlert(!alert);
		props.onClose();
	  }

	let color = null;
    let strong = null;
    switch (props.alert.type) {
        case alert.SUCCESS:
            color = "success";
            strong = "Well done!"
          break;
        case alert.ERROR:
            color = "danger";
            strong = "Oh Snap!"
          break;
        case alert.INFO:
            color = "info";
            strong = "Heads Up!"
          break;
        case alert.WARNING:
            color = "warning";
            strong = "Warning!"
          break;
      }
    if(!color || !strong) return null;

	return (
        <Alert color={color} className={`bg-${color} text-white`} isOpen={alert} toggle={() => close()}>
            <strong>{strong}</strong> {props.alert.message}
        </Alert>
    );

}

export default AlertBox;
