import React , {useEffect, useState} from "react";
import AddDriver from './AddDriver';
import Address from './info/Address';
import BasicInfo from './info/BasicInfo';
import VehicleInfo from './info/VehicleInfo';
import LicenseInfo from './info/LicenseInfo';
import Documents from './info/Documents';
import {useLocation} from "react-router-dom";

import {
    Breadcrumb,
    BreadcrumbItem,
    Button,
    Card,
    CardBody,
    CardTitle,
    Container,
    Row,
    Col,
    FormGroup,
    Input
  } from "reactstrap";

function Driver(props){
  const location = useLocation();
  const[editMode, setEditMode]=useState(true);
  const driver = location.driver;

  const editHandler = () =>{
      setEditMode = !editMode;
  }
	return(
    <>
      <div className="add-driver mt-5">
        <Container fluid>
        { editMode ?
                <Row>
                    <Col>
                        <div id="edit-btn float-right">
                          <Button
                            onClick={editHandler}
                            className="btn btn-warning"
                          >
                            Edit details
                        </Button>
                    </div>
                    </Col>
                </Row>:null
            }
          <Row>
  	        <BasicInfo driver={driver}/>
            <Address/>
          </Row>
          <Row>
            <VehicleInfo/>
            <Col lg="6">
              <LicenseInfo/>
              <Documents/>
            </Col>
          </Row>
        </Container>
      </div>
    </>
	);
}


export default Driver;
