import React, { useState, useEffect} from 'react';
import { MDBDataTableV5 } from 'mdbreact';
import { Button, Container, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { Link } from 'react-router-dom';
import './driver.scss';
import { connect } from "react-redux";
import { fetchDriver, deleteDriver, updateDriver } from '../../store/actions';
import AddDriver from './AddDriver';
import API from "../../helpers/api";

function DriverList(props) {
  const [data, setData] = useState([]);
  const [modalShow, setModalShow] = useState(false);
  const [drivers, setDrivers] = useState([]);
  // const [showDriverEditForm, setShowDriverEditForm] = useState(false);
  // const [selectedDriver, setSelectedDriver] = useState(null);

  //retrive all Drivers
  const retriveDrivers = async () => {
    const response = await API('drivers','GET');
    return response;
  }
  const handleDelete =  (event, driver) => {
      // setDriver(driver);
      // setModalShow(true);
  }
  const handleClose = () => {
    // setModalShow(false);
  }
  const handleDeleteDriver = (event, id) =>{
      // props.deleteDriver(id, props.drivers)
      // setModalShow(false);
  }
  //   }
  const handleEdit = (event, driver) =>{
    // setShowDriverEditForm(true)
    // setDriver(driver);
  }
  const handleView = (event, driver) =>{
    console.log("view");
  }
  const toggle = () => setModalShow(!modalShow);
  // console.log(props.drivers)
  let newList = [];
  if (drivers){
       newList = drivers.map(driver => {
        return Object.assign(driver, {
            action: 
            <>
              <Link to={`driver/${driver.id}`}><button className="btn btn-info">View</button></Link>
              <button className="btn btn-danger"  onClick={(event) => handleDelete(event, driver)}>Delete</button>
            </>
        });
    });
  }

  const newData={
    columns: [
      {
        label: 'Name',
        field: 'name',
        width: 100,
      },
      {
        label: 'Email',
        field: 'email',
        width: 100,
      },
      {
        label: 'Address',
        field: 'address',
        width: 100,
      },
      {
        label: 'Phone No.',
        field: 'phone',
        width: 100,
      },
      {
        label: 'Action',
        field: 'action',
        width: 200,
      },
    ],
    rows: newList
  };

  useEffect(() => {
      const getAllDrivers = async () => {
        const allDrivers = await retriveDrivers();
        if(allDrivers) setDrivers(allDrivers);
      }
      getAllDrivers();
  },[]);

    return(
    <>
      <div className="driver-list">
        <Container fluid>
          <h1>driver list</h1>
          <MDBDataTableV5 hover entriesOptions={[5, 20, 25]} entries={20} pagesAmount={4} data={newData} />
          <Modal isOpen={modalShow} toggle={toggle} className="d">
            <ModalHeader toggle={toggle}>Delete Driver</ModalHeader>
            <ModalBody>
              Are you sure to delete driver?
            </ModalBody>
            <ModalFooter>
              <Button className="btn-danger" onClick={(event) => handleDeleteDriver(event)}>Delete</Button>{' '}
              <Button className="btn-info" onClick={toggle}>Cancel</Button>
            </ModalFooter>
          </Modal>
        </Container>
      </div>
    </>
  )
}
export default DriverList;
