import { LOGIN, LOGIN_SUCCESSFUL, LOGIN_ERROR } from "./actionTypes";

const initialState = {
    loading: null, errorMessage: null
}

const login = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN:
            state = {
                ...state,
                user: null,
                loading: true,
                errorMessage: null
            }
            break;
        case LOGIN_SUCCESSFUL:
            state = {
                user: action.payload,
                loading: false,
                errorMessage: null
            }
            break;
        case LOGIN_ERROR:
            state = {
                ...state,
                loading: false,
                errorMessage: action.payload
            }
            break;
        default:
            state = { ...state };
            break;
    }
    return state;
}
export default login;
