import {
    Breadcrumb,
    BreadcrumbItem,
    Button,
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    Container,
    Row,
    Col,
    FormGroup,
    Input,
    Label
  } from "reactstrap";
import { useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";
import React, { useEffect, useState} from 'react';
import _ from "lodash/fp";
import '../driver.scss';
import API from '../../../helpers/api';
import * as yup from 'yup';

const AddDriverSchema = yup.object().shape({
  firstName: yup.string().required(),
  lastName: yup.string().required(),
  mobile: yup
    .number()
    .required()
    .positive()
    .integer(),
  email:yup.string().email()
});


  function AddDriver(props){
    const { register, handleSubmit, watch, errors } = useForm({
        mode: 'onChange',
        validationSchema: AddDriverSchema
    });
    const history = useHistory();
    const[drivers, setDrivers]=useState([]);

    const OnSubmit = async (data)  => {
      const response = await API('drivers', 'POST', data);
      if(response.id){
        history.push('/driver-list');
      }
    }
    return(
        <>
            <Col lg="6">
                <Card>
                    <CardHeader>
                        <h3 className="mb-0">Add Driver</h3>
                    </CardHeader>
                    <CardBody>
                        <form onSubmit={handleSubmit(OnSubmit)}>
                            <FormGroup className="row">
                                <Col md="3">
                                    <Label
                                        className="form-control-label col-form-label"
                                        htmlFor="firstName"
                                    >
                                    First Name
                                    </Label>
                                </Col>
                                <Col md="9">
                                    <Input
                                        name="firstName"
                                        className={errors.firstName?"is-invalid":""}
                                        placeholder="First Name"
                                        type="text"                                                
                                        innerRef={register({
                                        required: true,
                                        pattern: /^[A-Za-z]+[A-Za-z]/i 
                                        })}
                                    />
                                    {_.get("firstName.type", errors) === "required" && (
                                        <p className="error-message">This field is required</p>
                                    )}
                                    {_.get("firstName.type", errors) === "pattern" && (
                                        <p className="error-message">Name should be letters only</p>
                                    )}
                                </Col>
                            </FormGroup>
                            <FormGroup className="row">
                                <Col md="3">
                                    <Label
                                        className="form-control-label col-form-label"
                                        htmlFor="lastName"
                                    >
                                    Last Name
                                    </Label>
                                </Col>
                                <Col md="9">
                                    <Input
                                        name="lastName"
                                        className={errors.lastName?"is-invalid":""}
                                        placeholder="Last Name"
                                        type="text"
                                        innerRef={register({
                                        required: true,
                                        pattern: /^[A-Za-z]+[A-Za-z]/i 
                                        })}
                                    />
                                    {_.get("lastName.type", errors) === "required" && (
                                        <p className="error-message">This field is required</p>
                                    )}
                                    {_.get("lastName.type", errors) === "pattern" && (
                                        <p className="error-message">Name should be letters only</p>
                                    )}
                                </Col>
                            </FormGroup>
                            <FormGroup className="row">
                                <Col md="3">
                                    <Label
                                        className="form-control-label col-form-label"
                                        htmlFor="email"
                                    >
                                    Email
                                    </Label>
                                </Col>
                                <Col md="9">
                                    <Input
                                        name="email"
                                        className={errors.email?"is-invalid":""}
                                        placeholder="Email"
                                        type="email"
                                        innerRef={register({
                                        required: true,
                                        pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i
                                      })}
                                    />
                                    {_.get("email.type", errors) === "required" && (
                                        <p className="error-message">This field is required</p>
                                    )}
                                    {_.get("email.type", errors) === "pattern" && (
                                        <p className="error-message">Invalid Email</p>
                                    )}
                                </Col>
                            </FormGroup>
                            <FormGroup className="row">
                                <Col md="3">
                                    <Label
                                        className="form-control-label col-form-label"
                                        htmlFor="mobile"
                                    >
                                    Mobile
                                    </Label>
                                </Col>
                                <Col md="9">
                                    <Input
                                        name="mobile"
                                        className={errors.mobile?"is-invalid":""}
                                        placeholder="Phone No"
                                        type="number"
                                        innerRef={register({
                                            required: true,
                                            pattern: /^(98|97)([0-9]{8}$)/i
                                        })}
                                    />
                                   {_.get("mobile.type", errors) === "required" && (
                                        <div className="invalid-feedback">This field is required</div>
                                    )}
                                    {_.get("mobile.type", errors) === "pattern" && (
                                        <p className="error-message">10 digit number and starts with 98 or 97</p>
                                    )}
                                </Col>
                            </FormGroup>
                            <Row className="float-right">
                                <Col>
                                    <Button type="submit" className="btn-info">Submit</Button>
                                </Col>
                            </Row>
                        </form>
                    </CardBody>
                </Card>
            </Col>
        </>
    );
}

export default AddDriver;
