import React , {useEffect, useState} from "react";
import AddDriver from './AddDriver';
import Address from './info/Address';
import BasicInfo from './info/BasicInfo';
import VehicleInfo from './info/VehicleInfo';
import LicenseInfo from './info/LicenseInfo';
import Documents from './info/Documents';
import {useLocation, useParams} from "react-router-dom";
import API from '../../helpers/api';


import {
    Breadcrumb,
    BreadcrumbItem,
    Button,
    Card,
    CardBody,
    CardTitle,
    Container,
    Row,
    Col,
    FormGroup,
    Input
  } from "reactstrap";

function Driver(props){
  const[editMode, setEditMode]=useState(false);
  const[driver, setDriver] = useState('');
  const params = useParams();
  const editHandler = () =>{
      setEditMode (!editMode);
  }

  useEffect(()=> { 
    const data = fetchDriver(params.id)
       setDriver(data);
  },[])

  const fetchDriver = async (id) => {
    const data = await API(`drivers/${id}`, "GET");
  console.log(data);
    return data;
  }

	return(
    <>
      <div className="add-driver mt-5">
        <Container fluid>
        { !editMode ?
                <Row>
                    <Col>
                        <div className="float-right mb-2">
                          <Button
                            onClick={editHandler}
                            className="btn btn-warning"
                          >
                            Edit details
                        </Button>
                    </div>
                    </Col>
                </Row>:null
            }
          <Row>
  	        <BasicInfo id={params.id} driver={driver} editMode={editMode} />
            <Address id={params.id} driver={driver} editMode={editMode}/>
          </Row>
          <Row>
            <VehicleInfo id={params.id} driver={driver} editMode={editMode}/>
            <Col lg="6">
              <LicenseInfo id={params.id} driver={driver} editMode={editMode}/>
              <Documents id={params.id} driver={driver} editMode={editMode}/>
            </Col>
          </Row>
        </Container>
      </div>
    </>
	);
}


export default Driver;
