import React, { useState } from 'react';
import { Alert} from 'reactstrap';
import * as alertMessage from './AlertMessages';

function AlertBox(props){
	// const[alert, setAlert] = useState(false)

  	// const onDismiss = () => setVisible(false);
	// const close=()=>{
	// 	setAlert(!alert);
	// 	props.onClose();
	//   }

	const parseColor=()=>{
		switch (props.type) {
	        case alertMessage.SUCCESS:
	           return "success";
	        case alertMessage.ERROR:
	            return "danger";
	        case alertMessage.INFO:
	            return "info";
	        case alertMessage.WARNING:
	            return "warning";
	      }
	}


	let color = null;
    let strong = null;
    switch (props.type) {
        case alertMessage.SUCCESS:
            color = "success";
            strong = "Well done!"
          break;
        case alertMessage.ERROR:
            color = "danger";
            strong = "Oh Snap!"
          break;
        case alertMessage.INFO:
            color = "info";
            strong = "Heads Up!"
          break;
        case alertMessage.WARNING:
            color = "warning";
            strong = "Warning!"
          break;
      }
    if(!color || !strong) return null;

	return (
        <Alert color={color} isOpen={props.showAlert}>
	      <strong>dgdgdg</strong> {props.message}
	    </Alert>
    );

}

export default AlertBox;
