import {
    Breadcrumb,
    BreadcrumbItem,
    Button,
    Card,
    CardBody,
    CardTitle,
    Container,
    Row,
    Col,
    FormGroup,
    Input
  } from "reactstrap";
import { useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";
import React, { useEffect, useState} from 'react';
import _ from "lodash/fp";
import './driver.scss';
import { connect } from "react-redux";
import { addDriver } from '../../store/actions';


  function AddDriver(props){
    const { register, handleSubmit, watch, errors } = useForm();
    const history = useHistory();

    const[name, setName] = useState(null);
    const[address, setAddress] = useState(null);
    const[email, setEmail] = useState(null);
    const[phone, setPhone] = useState(null);
    const[editMode, setEditMode] = useState(false);

    const onSubmit =  data  => {
      props.addDriver(data, history)
    }

    useEffect(()=>{
    },[props.driver])
    return(
        <>
            <div className="add-driver">
                <Container fluid>
                    <h1>{editMode?'Add Driver':'Update User'}</h1>
                    {!editMode ?
                    <Card className="mb-4">
                        <CardBody>
                            <form onSubmit={handleSubmit(onSubmit)}>
                                <Row>
                                    <Col md="4">
                                        <FormGroup>
                                            <label
                                              className="form-control-label"
                                              htmlFor="firstname"
                                            >
                                            Name
                                            </label>
                                            <Input
                                              id="name"
                                              name="name"
                                              placeholder="Name"
                                              type="text"
                                              // value={props.driver.name ? props.driver.name : ''}
                                              innerRef={register({
                                                  required: true,
                                                  pattern: /^[A-Za-z]+[A-Za-z]/i 
                                                })}
                                            />
                                            {_.get("name.type", errors) === "required" && (
                                                <p className="error-message">This field is required</p>
                                            )}
                                            {_.get("name.type", errors) === "pattern" && (
                                                <p className="error-message">Name should be letters only</p>
                                            )}
                                        </FormGroup>
                                    </Col>
                                    <Col md="4">
                                        <FormGroup>
                                            <label
                                              className="form-control-label"
                                              htmlFor="email"
                                            >
                                            Email
                                            </label>
                                            <Input
                                              name="email"
                                              placeholder="Email"
                                              type="email"
                                              // value={props.driver.email ? props.driver.email : ''}
                                              innerRef={register({
                                              required: true,
                                              pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i
                                            })}
                                            />
                                            {_.get("email.type", errors) === "required" && (
                                                <p className="error-message">This field is required</p>
                                            )}
                                            {_.get("email.type", errors) === "pattern" && (
                                               <p className="error-message">Invalid Email</p>
                                            )}
                                        </FormGroup>
                                    </Col>
                                    <Col md="4">
                                        <FormGroup>
                                            <label
                                              className="form-control-label"
                                              htmlFor="lastname"
                                            >
                                            Address
                                            </label>
                                            <Input
                                              name="address"
                                              placeholder="Address"
                                              // value={props.driver.address ? props.driver.address : ''}
                                              type="text"
                                              innerRef={register({
                                                  required: true,
                                                })}
                                            />
                                             {_.get("address.type", errors) === "required" && (
                                                <p className="error-message">This field is required</p>
                                            )}
                                        </FormGroup>
                                    </Col>
                                </Row>
                                <Row>

                                    <Col md="4">
                                        <FormGroup>
                                            <label
                                              className="form-control-label"
                                              htmlFor="lastname"
                                            >
                                            Phone
                                            </label>
                                            <Input
                                              name="phone"
                                              placeholder="Phone No"
                                              type="number"
                                              // value={props.driver.phone ? props.driver.phone : ''}
                                              innerRef={register({
                                                  required: true,
                                                })}
                                            />
                                             {_.get("phone.type", errors) === "required" && (
                                                <p className="error-message">This field is required</p>
                                            )}
                                        </FormGroup>
                                    </Col>
                                </Row>
                                <Row>
                                    <Col>
                                        <Button type="submit" className="btn-info">Submit</Button>
                                    </Col>
                                </Row>
                            </form>
                        </CardBody>
                    </Card>:''}
                </Container>
            </div>
        </>
    );
}


const mapStateToProps = (state) => {
  return {
    loading: state.login.loading,
  };
};

export default connect(mapStateToProps, { addDriver })(AddDriver);
