import React , {useEffect, useState} from "react";
import AddDriver from './AddDriver';
import { useForm } from "react-hook-form";
import _ from "lodash/fp";
import { fetchDriver, switchEditMode } from '../../store/actions';
import { connect } from "react-redux";
import {
    Breadcrumb,
    BreadcrumbItem,
    Button,
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    Container,
    Row,
    Col,
    FormGroup,
    Input,
    Label
  } from "reactstrap";


function Driver(props){
  const history = useHistory();
  const[drivers, setDrivers]=useState([]);
  const[firstNameClass, setFirstNameClass] = useState('');
  const[lastNameClass, setLastNameClass] = useState('');
  const[emailClass, setEmailClass] = useState('');
  const[mobileClass, setMobileClass] = useState('');
	const { register, handleSubmit, watch, errors } = useForm();
	const driverId =  props.match.params.id;
  const OnSubmit = async (data)  => {
      const response = await API('drivers', 'POST', data);
      if(response.id){
        history.push('/driver-list');
      }
    }
	return(
		<div className="view-driver">
                <Container className="mt--5" fluid>
                    <Row>
                        <Col lg="6">
                            <Card>
                                <CardHeader>
                                    <h3 className="mb-0">Add Driver</h3>
                                </CardHeader>
                                <CardBody>
                                    <form onSubmit={handleSubmit(OnSubmit)}>
                                        <FormGroup className="row">
                                            <Col md="3">
                                                <Label
                                                    className="form-control-label col-form-label"
                                                    htmlFor="firstname"
                                                >
                                                First Name
                                                </Label>
                                            </Col>
                                            <Col md="9">
                                                <Input
                                                    name="firstname"
                                                    className={firstNameClass}
                                                    placeholder="First Name"
                                                    type="text"
                                                    onChange = {(e)=>{
                                                        if(e.target.value.length > 0){
                                                            setFirstNameClass(errors.firstname?"is-invalid":"is-valid")
                                                        }
                                                        else setFirstNameClass("is-invalid")
                                                    }}                                                
                                                    innerRef={register({
                                                    required: true,
                                                    pattern: /^[A-Za-z]+[A-Za-z]/i 
                                                    })}
                                                />
                                                {_.get("firstname.type", errors) === "required" && (
                                                    <p className="error-message">This field is required</p>
                                                )}
                                                {_.get("firstname.type", errors) === "pattern" && (
                                                    <p className="error-message">Name should be letters only</p>
                                                )}
                                            </Col>
                                        </FormGroup>
                                        <FormGroup className="row">
                                            <Col md="3">
                                                <Label
                                                    className="form-control-label col-form-label"
                                                    htmlFor="lastname"
                                                >
                                                Last Name
                                                </Label>
                                            </Col>
                                            <Col md="9">
                                                <Input
                                                    name="lastname"
                                                    className={lastNameClass}
                                                    onChange = {(e)=>{
                                                        if(e.target.value.length > 0){
                                                            setLastNameClass(errors.lastname?"is-invalid":"is-valid")
                                                        }
                                                        else setLastNameClass("is-invalid")
                                                    }}     
                                                    placeholder="Last Name"
                                                    type="text"
                                                    innerRef={register({
                                                    required: true,
                                                    pattern: /^[A-Za-z]+[A-Za-z]/i 
                                                    })}
                                                />
                                                {_.get("lastname.type", errors) === "required" && (
                                                    <p className="error-message">This field is required</p>
                                                )}
                                                {_.get("lastname.type", errors) === "pattern" && (
                                                    <p className="error-message">Name should be letters only</p>
                                                )}
                                            </Col>
                                        </FormGroup>
                                        <FormGroup className="row">
                                            <Col md="3">
                                                <Label
                                                    className="form-control-label col-form-label"
                                                    htmlFor="email"
                                                >
                                                Email
                                                </Label>
                                            </Col>
                                            <Col md="9">
                                                <Input
                                                    name="email"
                                                    className={emailClass}
                                                    onChange = {(e)=>{
                                                        if(e.target.value.length > 0){
                                                            setEmailClass(errors.email?"is-invalid":"is-valid")
                                                        }
                                                        else setEmailClass("is-invalid")
                                                    }}  
                                                    placeholder="Email"
                                                    type="email"
                                                    innerRef={register({
                                                    required: true,
                                                    pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i
                                                  })}
                                                />
                                                {_.get("email.type", errors) === "required" && (
                                                    <p className="error-message">This field is required</p>
                                                )}
                                                {_.get("email.type", errors) === "pattern" && (
                                                    <p className="error-message">Invalid Email</p>
                                                )}
                                            </Col>
                                        </FormGroup>
                                        <FormGroup className="row">
                                            <Col md="3">
                                                <Label
                                                    className="form-control-label col-form-label"
                                                    htmlFor="mobile"
                                                >
                                                Mobile
                                                </Label>
                                            </Col>
                                            <Col md="9">
                                                <Input
                                                    name="mobile"
                                                    className={mobileClass}
                                                    onChange = {(e)=>{
                                                        if(e.target.value.length > 0){
                                                            setMobileClass(errors.mobile?"is-invalid":"is-valid")
                                                        }
                                                        else setMobileClass("is-invalid")
                                                    }} 
                                                    placeholder="Phone No"
                                                    type="number"
                                                    innerRef={register({
                                                        required: true,
                                                        pattern: /^(98|97)([0-9]{8}$)/i
                                                    })}
                                                />
                                               {_.get("mobile.type", errors) === "required" && (
                                                    <p className="error-message">This field is required</p>
                                                )}
                                                {_.get("mobile.type", errors) === "pattern" && (
                                                    <p className="error-message">10 digit number and starts with 98 or 97</p>
                                                )}
                                            </Col>
                                        </FormGroup>
                                        <Row className="float-right">
                                            <Col>
                                                <Button type="submit" className="btn-info">Submit</Button>
                                            </Col>
                                        </Row>
                                    </form>
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </Container>
    </div>
	);
}

export default Driver;
