import React from "react";
import classnames from "classnames";
import "./login.scss";
import _ from "lodash/fp";
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col
} from "reactstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelopeOpenText, faLockOpen} from '@fortawesome/free-solid-svg-icons';
import { useForm } from "react-hook-form";

function Login () {

  const { register, handleSubmit, errors} = useForm();
  const onSubmit = data => {
    alert(JSON.stringify(data));
  };
    return (
      <>
        <div className="login-form-wrapper">
          <Container className="pb-5">
            <Row className="justify-content-center">
              <Col lg="5" md="7">
                <Card className="border-0 mb-0">
                  <CardBody className="px-lg-5 py-lg-5">
                    <div className="text-center text-muted mb-4">
                      <h1>Login Form</h1>
                    </div>
                  <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="input-wrapper text-muted">
                      <FontAwesomeIcon icon={faEnvelopeOpenText}/>
                      <input
                        placeholder="Email"
                        name="email"
                        type="email"
                        ref={register({
                          required:true,
                          pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i
                        })}
                      />
                      {_.get("email.type", errors) === "required" && (
                        <p>This field is required</p>
                      )}
                      {_.get("email.type", errors) === "pattern" && (
                        <p>Email must contain @</p>
                      )}
                    </div>
                    <div className="input-wrapper text-muted">
                     <FontAwesomeIcon icon={faLockOpen}/>
                      <input
                        placeholder="Password"
                        name="password"
                        type="password"
                        ref={register({
                          required:true,
                          pattern: /^[A-Za-z]\w{7,14}$/
                        })}
                      />
                      {_.get("password.type", errors) === "required" && (
                        <p>This field is required</p>
                      )}
                      {_.get("password.type", errors) === "pattern" && (
                        <p>Password must 8 characters</p>
                      )}
                    </div>
                     <div className="custom-control custom-control-alternative custom-checkbox">
                      <input
                        className="custom-control-input"
                        id=" customCheckLogin"
                        type="checkbox"
                      />
                      <label
                        className="custom-control-label"
                        htmlFor=" customCheckLogin"
                      >
                        <span className="text-muted">Remember me</span>
                      </label>
                    </div>
                    <div className="text-center">
                      <Button type="submit" className="btn-info">Login</Button>
                    </div>
                  </form>
                  </CardBody>
                </Card>
                <Row className="mt-3">
                  <Col xs="6">
                    <a
                      className="text-light"
                      href="#pablo"
                      onClick={e => e.preventDefault()}
                    >
                      <small>Forgot password?</small>
                    </a>
                  </Col>
                </Row>
              </Col>
            </Row>
          </Container>
        </div>
      </>
    );
}

export default Login;
