import {
    Breadcrumb,
    BreadcrumbItem,
    Button,
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    Container,
    Row,
    Col,
    FormGroup,
    Input,
    Label
  } from "reactstrap";
import { useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";
import React, { useEffect, useState} from 'react';
import _ from "lodash/fp";
import '../driver.scss';
import API from '../../../helpers/api';
import * as yup from 'yup';
import { yupResolver } from '@hookform/resolvers/yup';

const AddDriverSchema = yup.object().shape({
  firstName: yup.string().required(),
  lastName: yup.string().required(),
  email: yup.string().email().required(),
  mobile: yup.string()
    .required()
    .matches(/^[0-9]+$/, "Must be only digits")
    .min(10)
    .max(10)
});


  function AddDriver(props){
    const { register, handleSubmit, watch, errors } = useForm({
        mode: 'onChange',
        resolver: yupResolver(AddDriverSchema)
    });
    const history = useHistory();
    const OnSubmit = async (data)  => {
      const response = await API('drivers', 'POST', data);
      if(response.id){
        history.push('/driver-list');
      }
    }


    return(
        <>
            <Col lg="6">
                <Card>
                    <CardHeader>
                        <h3 className="mb-0">Add Driver</h3>
                    </CardHeader>
                    <CardBody>
                        <form onSubmit={handleSubmit(OnSubmit)}>
                            <FormGroup className="row">
                                <Col md="3">
                                    <Label
                                        className="form-control-label col-form-label"
                                        htmlFor="firstName"
                                    >
                                    First Name
                                    </Label>
                                </Col>
                                <Col md="9">
                                    <Input
                                        name="firstName"
                                        className={errors.firstName?"is-invalid":""}
                                        placeholder="First Name"
                                        type="text" 
                                        value={props.driver?props.driver.firstName:''}
                                        disabled={props.driver?!props.editMode:''}                                             
                                        innerRef={register}
                                    />
                                    {errors.firstName && <div className="invalid-feedback">{errors.firstName.message}</div>}
                                </Col>
                            </FormGroup>
                            <FormGroup className="row">
                                <Col md="3">
                                    <Label
                                        className="form-control-label col-form-label"
                                        htmlFor="lastName"
                                    >
                                    Last Name
                                    </Label>
                                </Col>
                                <Col md="9">
                                    <Input
                                        name="lastName"
                                        className={errors.lastName?"is-invalid":""}
                                        placeholder="Last Name"
                                        type="text"
                                        value={props.driver?props.driver.lastName:''}
                                        disabled={props.driver?!props.editMode:''} 
                                        innerRef={register}
                                    />
                                   {errors.lastName && <div className="invalid-feedback">{errors.lastName.message}</div>}
                                </Col>
                            </FormGroup>
                            <FormGroup className="row">
                                <Col md="3">
                                    <Label
                                        className="form-control-label col-form-label"
                                        htmlFor="email"
                                    >
                                    Email
                                    </Label>
                                </Col>
                                <Col md="9">
                                    <Input
                                        name="email"
                                        className={errors.email?"is-invalid":""}
                                        placeholder="Email"
                                        type="email"
                                        value={props.driver?props.driver.email:''}
                                        disabled={props.driver?!props.editMode:''} 
                                        innerRef={register}
                                    />
                                    {errors.email && <div className="invalid-feedback">{errors.email.message}</div>}
                                </Col>
                            </FormGroup>
                            <FormGroup className="row">
                                <Col md="3">
                                    <Label
                                        className="form-control-label col-form-label"
                                        htmlFor="mobile"
                                    >
                                    Mobile
                                    </Label>
                                </Col>
                                <Col md="9">
                                    <Input
                                        name="mobile"
                                        className={errors.mobile?"is-invalid":""}
                                        placeholder="Phone No"
                                        type="number"
                                        value={props.driver?props.driver.mobile:''}
                                        disabled={props.driver?!props.editMode:''} 
                                        innerRef={register}
                                    />
                                    {errors.mobile && <div className="invalid-feedback">{errors.mobile.message}</div>}
                                </Col>
                            </FormGroup>
                            {props.editMode?
                                <Row className="float-right">
                                    <Col>
                                        <Button type="submit" className="btn-info">Update</Button>
                                    </Col>
                                </Row>
                            :""}
                        </form>
                    </CardBody>
                </Card>
            </Col>
        </>
    );
}

export default AddDriver;
