import { ADD_DRIVER, ADD_DRIVER_SUCCESSFUL, ADD_DRIVER_FAILED, FETCH_DRIVER, FETCH_DRIVER_SUCCESSFUL, FETCH_DRIVER_FAILED  } from "./actionTypes";

const initialState = {
    loading: null, errorMessage: null, drivers:null
}

const driver = (state = initialState, action) => {
    switch (action.type) {
        case ADD_DRIVER:
            state = {
                ...state,
                loading:true,
                isAuthenticated: true,
            }
            break;
        case ADD_DRIVER_SUCCESSFUL:
            state = {
                ...state,
                loading: false,
                errorMessage: null
            }
            break;
        case ADD_DRIVER_FAILED:
            state = {
                ...state,
                loading: false,
                errorMessage: null
            }
            break;
        case FETCH_DRIVER:
            state = {
                ...state,
                loading: true,
                errorMessage: null
            }
            break;
        case FETCH_DRIVER_SUCCESSFUL:
            state = {
                ...state,
                loading: false,
                errorMessage: null
            }
            break;
        case FETCH_DRIVER_FAILED:
            state = {
                ...state,
                loading: false,
                errorMessage: null
            }
            break;
        default:
            state = { ...state };
            break;
    }
    return state;
}
export default driver;
