import React ,{ useContext } from 'react';
import  {MDBDataTableV5}  from 'mdbreact';
import {GlobalContext} from  "../../db";
import {Button} from 'reactstrap';

function DriverList(){
	const {driverData ,removeDriver} = useContext(GlobalContext);
	const newList = driverData.map((item, idx)=>{
    return Object.assign(item, {action: 
      <>
      <Button className="btn btn-dark">Edit</Button>
      <Button className="btn btn-danger" onClick={() => removeDriver(item.id)}>Delete</Button>
      </>})
  });

	const [datatable, setDatatable] = React.useState({
    columns: [
      {
        label: 'Name',
        field: 'name',
        width: 150,
      },
      {
        label: 'Email',
        field: 'email',
        width: 150,
      },
      {
        label: 'Address',
        field: 'address',
        width: 150,
      },
      {
        label: 'Phone No.',
        field: 'phone',
        width: 150,
      },
      {
        label: 'Action',
        field: 'action',
        width: 200,
      },
    ],
    rows: newList
  });
	return(
		<>

			<h1>driver list</h1>
			<MDBDataTableV5 hover entriesOptions={[5, 20, 25]} entries={5} pagesAmount={4} data={datatable} />;
		</>
	);
}
export default DriverList;
