import React, { useState, useEffect} from 'react';
import { MDBDataTableV5 } from 'mdbreact';
import { Button, Container, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { Link } from 'react-router-dom';
import './driver.scss';
import { connect } from "react-redux";
import { fetchDriver, deleteDriver } from '../../store/actions';

function DriverList(props) {
  const [data, setData] = useState([])
  const [modalShow, setModalShow] = useState(false)

  const handleDelete =  (event, drivers) => {
    console.log(drivers)
      // setModalShow(true);
  }
  const handleClose = () => {
    setModalShow(false);
  }
  const handleDeleteDriver = () =>{
      props.deleteDriver()
  }
  const toggle = () => setModalShow(!modalShow);
  let newList = [];
  if (props.drivers){
       newList = props.drivers.map(drivers => {
        return Object.assign(drivers, {
            action: 
            <>
              <button className="btn btn-primary">Edit</button>
              <button className="btn btn-danger" onClick={(event) => handleDelete(event, drivers)}>Delete</button>
            </>
        });
    });
  }

  const newData={
    columns: [
      {
        label: 'Name',
        field: 'name',
        width: 100,
      },
      {
        label: 'Email',
        field: 'email',
        width: 100,
      },
      {
        label: 'Address',
        field: 'address',
        width: 100,
      },
      {
        label: 'Phone No.',
        field: 'phone',
        width: 100,
      },
      {
        label: 'Action',
        field: 'action',
        width: 200,
      },
    ],
    rows: newList
  };

  useEffect(() => {
      props.fetchDriver();
  },[]);

    return(
    <>
      <div className="driver-list">
        <Container fluid>
          <h1>driver list</h1>
          <MDBDataTableV5 hover entriesOptions={[5, 20, 25]} entries={20} pagesAmount={4} data={newData} />
          <Modal isOpen={modalShow} toggle={toggle} className="d">
        <ModalHeader toggle={toggle}>Delete Driver</ModalHeader>
        <ModalBody>
          Are you sure to delete driver?
        </ModalBody>
        <ModalFooter>
          <Button className="btn-danger" onClick={handleDeleteDriver}>Delete</Button>{' '}
          <Button className="btn-info" onClick={toggle}>Cancel</Button>
        </ModalFooter>
      </Modal>
        </Container>
      </div>
    </>
  )
}

const mapStateToProps = (state) => {
  return {
    drivers: state.driver.drivers,
  };
};
export default connect(mapStateToProps, { fetchDriver,deleteDriver })(DriverList);
